  outline: none;
  cursor: pointer;

  &.storybook-button--primary:hover {
    background-color: $button-hover-background-color;
    outline: none;
    cursor: pointer;
  }

  &.storybook-button--primary:active {
    background-color: $button-active-background-color;
    color: $button-hover-background-color;
    outline: none;
    cursor: pointer;
  }

  &.storybook-button--primary:disabled {
    background-color: #e8e8e8;
    outline: none;
    pointer-events: none;
  }
}


//small button
.storybook-button--small {
  font-size: 12px;
  padding: 3px 10px;
  cursor: pointer;
  line-height: 16px;
  border: none;
  outline: none;
  text-transform: uppercase;

  &.storybook-button--small:hover {
    background-color: $button-hover-background-color;
    outline: none;
  }

